import React, { useState, useEffect } from 'react';
import '../styles/Home.css';
import ScrollIndicator from './ScrollIndicator';
import ProjectCard from './ProjectCard';

function Home() {
  const [jobTitleIndex, setJobTitleIndex] = useState(0);
  const jobTitles = ['web developer', 'designer', 'content creator']; // Add your job titles here

  useEffect(() => {
    const intervalId = setInterval(() => {
      setJobTitleIndex((prevIndex) => (prevIndex + 1) % jobTitles.length);
    }, 2000); // Change the duration (in milliseconds) for text rotation

    return () => clearInterval(intervalId);
  }, [jobTitles.length]);


  return (
    <div className="home-container">
      <div className="text-container">
        <h1>Hi, my name is <span className="name">Sarah</span></h1>
        <h2>I'm a <span className="job-title">{jobTitles[jobTitleIndex]}</span></h2>
      </div>
      <ScrollIndicator />
      <div className="projects-container">
        <ProjectCard
          title="Project 1"
          description="Description of Project 1"
          image="/static/images/project1.jpg"
          link="https://example.com/project1"
        />
        <ProjectCard
          title="Project 2"
          description="Description of Project 2"
          image="/static/images/project2.jpg"
          link="https://example.com/project2"
        />
        {/* Add more ProjectCard components as needed */}
      </div>
    </div>
  );
}

export default Home;
